INSERT INTO cocktails (id, name, description, degree, volume, receipt, cocktail_group, basis_id, taste, is_updatable, is_deleted, is_favourite, picture) VALUES (1, 'Негрони', 'Хотите попробовать "Негрони"? Это крепкий горький коктейль на основе джина с добавлением биттера и красного вермута. Идеален как в качестве аперитива, так и дижестива. Один из самых популярных коктейлей в мире, потрясающе вкусный и гармоничный. ', 9, 240, 'Наполни рокс кубиками льда доверху/||/Налей в бокал красный вермут 30 мл и красный биттер 30 мл/||/Добавь джин 30 мл и размешай коктейльной ложкой/||/Укрась кружком апельсина', 'крепкие, горькие, на джине, классика, негрони', 8, 'bitter', 1, 0, 0, 'Негрони.jpg');
INSERT INTO cocktails (id, name, description, degree, volume, receipt, cocktail_group, basis_id, taste, is_updatable, is_deleted, is_favourite, picture) VALUES (2, 'Манхэттен', 'Хотите попробовать "Манхэттен"? Это классический коктейль на основе бурбона с добавлением красного вермута. На вкус "Манхэттен" сладковатый с травяными нотками, которые привносит вермут и капля ангостуры. Кстати, ходят слухи, что придумала его мать Уинстона Черчилля. Возможно, и сам Премьер любил пропустить бокальчик этого замечательного напитка.', 5, 381, 'Налей в стакан для смешивания красный вермут 25 мл и бурбон 50 мл/||/Добавь ангостуру биттер 1 дэш/||/Наполни стакан кубиками льда и размешай коктейльной ложкой/||/Перелей через стрейнер в охлажденный коктейльный бокал/||/Укрась коктейльной вишней', 'крепкие, сладкие, на бурбоне, классика, красные', 1, 'sweet', 1, 0, 0, 'Манхэттен.jpg');
INSERT INTO cocktails (id, name, description, degree, volume, receipt, cocktail_group, basis_id, taste, is_updatable, is_deleted, is_favourite, picture) VALUES (3, 'Джин тоник', ' Хотите попробовать "Джин тоник"? Это бессмертный микс джина и тоника, который подается, наверное, в каждом баре Земли. Травяной и немного горький коктейль прекрасно освежает и тонизирует. Украсить его можно лаймом или огурцом в зависимости от того, преобладание чего вам больше нравится - свежести или кислинки.', 5, 400, 'Наполни хайбол кубиками льда доверху/||/Налей джин 50 мл/||/Долей тоник доверху и аккуратно размешай коктейльной ложкой/||/Укрась кружками лайма', 'слабоалкогольные, горькие, на джине, миксы, лонги', 8, 'bitter', 1, 0, 0, 'Джин тоник.jpg');
INSERT INTO cocktails (id, name, description, degree, volume, receipt, cocktail_group, basis_id, taste, is_updatable, is_deleted, is_favourite, picture) VALUES (4, 'Голубая лагуна', 'Хотите попробовать "Голубую лагуну"? Это сладкий освежающий лонг на водке с добавлением ликёра блю кюрасао,  который придаёт коктейлю нежный голубой оттенок. Есть легенда, что "Голубую лагуну" придумал художник Поль Гоген на Таити, но вряд ли он предложил бы добавлять в коктейль спрайт.', 4, 450, 'Наполни харрикейн кубиками льда доверху/||/Налей ликер блю кюрасао 20 мл и водку 50 мл/||/Долей спрайт доверху и аккуратно размешай коктейльной ложкой/||/Укрась долькой ананаса', 'слабоалкогольные, цитрусовые, тропические, сладкие, на водке', 7, 'citrus', 1, 0, 0, 'Голубая лагуна.jpg');
INSERT INTO cocktails (id, name, description, degree, volume, receipt, cocktail_group, basis_id, taste, is_updatable, is_deleted, is_favourite, picture) VALUES (5, 'Белый русский', 'Хотите попробовать "Белый русский"? Это крепкий кофейно-сливочный коктейль на водке. Когда-то он считался женским, но после выхода фильма "Большой Лебовски" стал любимым напитком всех "чуваков". Если не смотрели фильм братьев Коэнов, то выпейте бокал"Белого русского" и срочно исправьте эту оплошность.', 5, 210, 'Наполни рокс кубиками льда доверху/||/Налей в бокал нежирные сливки 30 мл, кофейный ликер 30 мл и водку 30 мл/||/Размешай коктейльной ложкой, пока не замерзнут стенки', 'крепкие, кофейные, сливочные, сладкие, на водке', 7, 'coffee', 1, 0, 0, 'Белый русский.jpg');
INSERT INTO cocktails (id, name, description, degree, volume, receipt, cocktail_group, basis_id, taste, is_updatable, is_deleted, is_favourite, picture) VALUES (6, 'Космополитен', 'Хотите попробовать "Космополитен"? Это кисло-сладкий коктейль на основе цитрусовой водки с добавлением трипл-сека и клюквенного сока. О нём знает каждый, кто хоть раз видел "Секс в большом городе". В сериале его в основном пьют женщины, но на самом деле этот коктейль любят люди во всем мире вне зависимости от пола.', 4, 321, 'Налей в шейкер лаймовый сок 10 мл, клюквенный сок 50 мл, ликер трипл сек 20 мл и цитрусовую водку 40 мл/||/Наполни шейкер кубиками льда и взбей/||/Перелей через стрейнер в охлажденный коктейльный бокал/||/Зажги над коктейлем горелку и выжми на нее масло из апельсиновой цедры/||/Проведи цедрой по краям бокала и укрась ею коктейль', 'крепкие, кислые, цитрусовые, на водке, классика', 19, 'sour', 1, 0, 0, 'Космополитен.jpg');
INSERT INTO cocktails (id, name, description, degree, volume, receipt, cocktail_group, basis_id, taste, is_updatable, is_deleted, is_favourite, picture) VALUES (7, 'Маргарита', 'Хотите попробовать маргариту? Это солоноватый кислый коктейль на текиле с лаймовым соком. Бармены во всем мире очень любят создавать твисты на этот классический коктейль, но окаёмка из соли практически всегда остаётся неизменным украшением "Маргариты".', 6, 327, 'Сделай на бокале для маргариты соленую окаемку/||/Налей в шейкер лаймовый сок 30 мл, сахарный сироп 10 мл, ликер трипл сек 25 мл и серебряную текилу 50 мл/||/Наполни шейкер кубиками льда и взбей/||/Перелей через стрейнер в охлажденный бокал для маргариты/||/Укрась кружком лайма', 'крепкие, кислые, цитрусовые, соленые, на текиле', 3, 'sour', 1, 0, 0, 'Маргарита.jpg');
INSERT INTO cocktails (id, name, description, degree, volume, receipt, cocktail_group, basis_id, taste, is_updatable, is_deleted, is_favourite, picture) VALUES (8, 'Секс на пляже', ' Хотите попробовать "Секс на пляже"? Это сладкий фруктовый лонг на водке с добавлением персикового ликёра. Коктейль прекрасно освежает и очень легко пьётся благодаря ананасовому соку, также входящему в его состав. "Секс на пляже" очень любили пить герои "Санта-Барбары", но сейчас он ассоциируется скорее с летом и шезлонгом.', 3, 525, 'Наполни слинг кубиками льда доверху/||/Налей в шейкер клюквенный сок 40 мл, ананасовый сок 40 мл, персиковый ликер 25 мл и водку 50 мл/||/Наполни шейкер кубиками льда и взбей/||/Перелей через стрейнер в слинг/||/Укрась кусочком ананаса и ягодой малины на коктейльной шпажке', 'крепкие, тропические, сладкие, на водке, лонги', 7, 'sweet', 1, 0, 0, 'Секс на пляже.jpg');
INSERT INTO cocktails (id, name, description, degree, volume, receipt, cocktail_group, basis_id, taste, is_updatable, is_deleted, is_favourite, picture) VALUES (9, 'Текила санрайз', ' Хотите попробовать "Текила Санрайз"? Это слабоалкогольный цитрусовый лонг на основе текилы с добавлением гренадина и апельсинового сока. Простой, но очень вкусный и красивый: переходящий в оранжевый цвет красный гренадин действительно напоминает восходящее солнце.', 4, 420, 'Наполни хайбол кубиками льда доверху/||/Налей гренадин 10 мл и серебряную текилу 50 мл/||/Долей апельсиновый сок доверху и аккуратно размешай коктейльной ложкой/||/Укрась кружком апельсина', 'слабоалкогольные, сладкие, цитрусовые, на текиле, простые', 3, 'sweet', 1, 0, 0, 'Текила санрайз.jpg');
INSERT INTO cocktails (id, name, description, degree, volume, receipt, cocktail_group, basis_id, taste, is_updatable, is_deleted, is_favourite, picture) VALUES (10, 'Лонг айленд айс ти', 'Хотите попробовать "Лонг айленд айс ти"? Это очень крепкий лонг на пяти видах алкоголя. Кроме водки, джина, рома, текилы и цитрусового ликёра, туда добавляют колу и лимонный сок, так что на вкус он получается сладковато-кислым. Коктейль придумали во времена сухого закона в Америке, маскируя его под чай, поэтому иногда его до сих пор подают в чайниках или френч-прессах.', 4, 610, 'Наполни френч-пресс кубиками льда доверху/||/Налей лимонный сок 30 мл, сахарный сироп 30 мл и ликер трипл сек 30 мл/||/Добавь водку 30 мл, джин 30 мл, белый ром 30 мл и серебряную текилу 30 мл/||/Долей колу доверху и аккуратно размешай коктейльной ложкой', 'крепкие, сладкие, классика, на компанию, физзы', 8, 'sweet', 1, 0, 0, 'Лонг айленд айс ти.jpg');
INSERT INTO cocktails (id, name, description, degree, volume, receipt, cocktail_group, basis_id, taste, is_updatable, is_deleted, is_favourite, picture) VALUES (11, 'Мохито', 'Это освежающий сладкий лонг на основе рома с большим количеством мяты и лайма. Кстати, когда-то их добавляли для того, чтобы перебить вкус плохого рома и обезопасить себя от болезней, но сегодня этого можно не бояться. Традиционно в коктейль добавляют газированную воду, но можно заменить её на спрайт, если вы предпочитаете более сладкий вкус.', 4, 448, 'Положи в хайбол лайм 3 дольки и подави мадлером/||/Возьми мяту 10 листиков в одну руку и хлопни по ним другой рукой/||/Положи мяту в хайбол/||/Наполни бокал дробленым льдом доверху/||/Добавь сахарный сироп 15 мл и белый ром 50 мл/||/Долей содовую доверху и аккуратно размешай коктейльной ложкой/||/Досыпь немного дробленого льда/||/Укрась веточкой мяты и долькой лайма', 'слабоалкогольные, мятные, кислые, цитрусовые, на роме', 9, 'sour', 1, 0, 0, 'Мохито.jpg');
INSERT INTO cocktails (id, name, description, degree, volume, receipt, cocktail_group, basis_id, taste, is_updatable, is_deleted, is_favourite, picture) VALUES (12, 'Кровавая Мэри', 'Хотите попробовать "Кровавую Мэри"? Это слабоалкогольный овощной лонг на водке с томатным соком и сельдереем. Напиток, в который также добавляют лимонный сок, табаско и ворчестер, когда-то придумали как лекарство против похмелья. Сегодня, правда, коктейль стал таким популярным в барах, что и сам нередко вызывает недуг, который был создан лечить.', 3, 579, 'Наполни хайбол кубиками льда доверху/||/Налей в шейкер лимонный сок 10 мл, томатный сок 120 мл и водку 50 мл/||/Добавь табаско соус красный 3 дэш и ворчестер соус 3 дэш/||/Приправь щепоткой сельдереевой соли и щепоткой черного перца молотого/||/Наполни шейкер льдом, закрой и перекатывай несколько минут из одной руки в другую по вертикали/||/Перелей через стрейнер в хайбол и укрась стеблем сельдерея', 'слабоалкогольные, пикантные, овощные, соленые, на водке', 7, 'salty', 1, 0, 0, 'Кровавая Мэри.jpg');
INSERT INTO cocktails (id, name, description, degree, volume, receipt, cocktail_group, basis_id, taste, is_updatable, is_deleted, is_favourite, picture) VALUES (20, 'Джоконда шутер', 'Вам нравятся простые шоты? Тогда попробуйте этот сладкий и пикантный коктейль на основе водки, алкогольный и крепкий. ', 21, 95, 'Налей в стопку домашнюю перечную водку 50 мл/||/Налей в другую стопку сахарный сироп 25 мл и окуни в него клубнику на шпажке/||/Выпей шот и заверши его клубникой', 'крепкие, сладкие, пикантные, на водке, шоты', 48, 'sweet', 1, 0, 0, 'Джоконда шутер.jpg');
INSERT INTO cocktails (id, name, description, degree, volume, receipt, cocktail_group, basis_id, taste, is_updatable, is_deleted, is_favourite, picture) VALUES (30, 'Кровь и песок', '', 4, 300, 'Налей в шейкер апельсиновый сок 25 мл, вишневый ликер 25 мл, красный вермут 25 мл и шотландский виски 25 мл/||/Наполни шейкер кубиками льда и взбей/||/Перелей через стрейнер в охлажденное шампанское блюдце', 'крепкие, цитрусовые, сладкие, на виски, классика', 11, 'citrus', 1, 0, 0, 'Кровь и песок.jpg');
INSERT INTO cocktails (id, name, description, degree, volume, receipt, cocktail_group, basis_id, taste, is_updatable, is_deleted, is_favourite, picture) VALUES (13, 'Апероль Шприц', 'Апероль стал всемирно популярен благодаря коктейлю Апероль Шприц. В чистом виде Апероль практически не употребляют уже почти 100 лет. Апероль Шприц считается «уроженцем» Италии, хотя право назвать его своим оспаривает Австрия. Апероль Шприц — фаворит среди коктейлей летнего сезона. На европейских курортах сложно найти заведение, где его не подают. Итальянский (классический) вариант Апероля содержит 11% алкоголя. Напиток, изготовленный для поставок в Германию, крепче на 4% из-за местного законодательства, согласно которому напитки с содержанием алкоголя менее 15% нужно разливать в многоразовые бутылки. В 2012 году Апероль завоевал золото в категории «Лучший аперитив» на San Francisco World Spirits Competition. Апероль Шприц — мега-популярный, но не единственный коктейль с Аперолем. Аперитив миксуют с цитрусовыми соками, игристыми винами, джином, водкой, или же просто подают со льдом и кусочками лимонов и апельсинов. В одной из старых рекламных кампаний аперитива производитель утверждал, что Апероль помогает дамам сохранить стройность. Одна из версий появления Апероль Шприц: австрийские солдаты делали коктейль из вина и газировки, чтобы утолять жажду, но не пьянеть. Название «Шприц» (Spritz) вероятнее всего произошло от немецкого spritzen (всплеск). Это же слово ассоциируется с газировкой, которую традиционно добавляют в Апероль Шприц.', 7, 320, 'Наполни бокал для вина льдом/||/Налей в бокал просекко 100 мл и апероль 100 мл/||/Добавь сплэш содовой и размешай коктейльной ложкой/||/Укрась долькой апельсина', 'слабоалкогольные, сладкие, на игристом, простые, физзы', 6, 'sweet', 1, 0, 0, 'Апероль Шприц.jpg');
INSERT INTO cocktails (id, name, description, degree, volume, receipt, cocktail_group, basis_id, taste, is_updatable, is_deleted, is_favourite, picture) VALUES (14, 'Б-52', 'Хотите попробовать "Б-52"? Это крепкий сладкий шот на кофейном ликёре с добавлением айриш крима и трипл сека. На вкус он сладкий и сливочно-кофейный. Верхний слой шота поджигается, и коктейль нужно быстро выпить через трубочку: не только вкусно, но и зрелищно.', 5, 45, 'Налей в стопку кофейный ликер 15 мл/||/Используя коктейльную ложку, уложи слой айриш крим 15 мл и слой ликера трипл сек 15 мл/||/Поджигай, вооружайся трубочками и угощай!', 'крепкие, сливочные, сладкие, на ликере, классика', 118, 'creamy', 1, 0, 0, 'Б-52.jpg');
INSERT INTO cocktails (id, name, description, degree, volume, receipt, cocktail_group, basis_id, taste, is_updatable, is_deleted, is_favourite, picture) VALUES (15, 'Лицо Гента', 'Вам нравятся шорты с нестандартной подачей? Тогда попробуйте этот цитрусовый, пряный, травяной и пикантный коктейль на основе пива, алкогольный и крепкий. ', 4, 447, 'Наполни глиняный горшок кубиками льда/||/Налей в шейкер лимонный сок 15 мл, апельсиновый сок 15 мл, сахарный сироп 10 мл и дженевер 45 мл/||/Добавь горчицу тирентьен 1 к. ложку и эликсир дАнверс 5 мл/||/Наполни шейкер кубиками льда и взбей/||/Перелей через стрейнер в охлажденный глиняный горшок/||/Долей стаут 15 мл и аккуратно размешай коктейльной ложкой/||/Укрась лимонной цедрой, листиком шалфея и кусочками сыра Па де Руж', 'крепкие, пикантные, пряные, травяные, цитрусовые', 25, 'spicy', 1, 0, 0, 'Лицо Гента.jpg');
INSERT INTO cocktails (id, name, description, degree, volume, receipt, cocktail_group, basis_id, taste, is_updatable, is_deleted, is_favourite, picture) VALUES (16, 'Тверская улица', 'Вам нравятся лонги и физзы? Тогда попробуйте этот ягодный, цитрусовый, пряный, пикантный и сладкий коктейль на основе текилы, алкогольный и крепкий. ', 3, 622, 'Наполни слинг кубиками льда доверху/||/Положи в шейкер дольку корня имбиря, мякоть небольшой дольки арбуза и подави мадлером/||/Налей мандариновый сироп 20 мл, лимончелло 15 мл и серебряную текилу 50 мл/||/Добавь перец горошком 6 шт./||/Наполни шейкер кубиками льда и взбей/||/Перелей через стрейнер и ситечко в слинг/||/Долей имбирное пиво доверху и аккуратно размешай коктейльной ложкой/||/Укрась кусочком арбуза', 'крепкие, цитрусовые, ягодные, пряные, сладкие', 3, 'citrus', 1, 0, 0, 'Тверская улица.jpg');
INSERT INTO cocktails (id, name, description, degree, volume, receipt, cocktail_group, basis_id, taste, is_updatable, is_deleted, is_favourite, picture) VALUES (17, 'Томми Ди сауэр', 'Вам нравятся твисты на классику и сауэры? Тогда попробуйте этот кислый и тропический коктейль на основе виски, алкогольный и крепкий. ', 4, 495, 'Наполни рокс кубиками льда доверху/||/Налей в шейкер белок перепелиного яйца 25 мл, лимонный сок 30 мл, сироп маракуйи 25 мл и шотландский виски 50 мл/||/Наполни шейкер кубиками льда и тщательно взбей/||/Перелей через стрейнер в рокс/||/Укрась половинкой маракуйи', 'крепкие, тропические, кислые, на виски, твисты', 11, 'sour', 1, 0, 0, 'Томми Ди сауэр.jpg');
