INSERT INTO cocktails (id, name, description, degree, volume, receipt, cocktail_group, basis_id, taste, is_updatable, is_deleted, is_favourite, picture) VALUES (107, 'Маргарита без сахара', 'Вам нравится маргарита? Тогда попробуйте Маргариту без сахара. Это кислый, цитрусовый и соленый коктейль на основе текилы, алкогольный и крепкий. ', 6, 307, 'Сделай на бокале соленую окаемку/||/Налей в шейкер лаймовый сок 15 мл, ликер трипл сек 30 мл и серебряную текилу 50 мл/||/Наполни шейкер кубиками льда и взбей/||/Перелей через стрейнер в охлажденный бокал для маргариты/||/Укрась долькой лайма', 'крепкие, цитрусовые, кислые, соленые, на текиле', 3, 'citrus', 1, 0, 0, 'Маргарита без сахара.jpg');
