INSERT INTO cocktails (id, name, description, degree, volume, receipt, cocktail_group, basis_id, taste, is_updatable, is_deleted, is_favourite, picture) VALUES (129, 'Кокосовая сангрия в кувшине', 'Вам нравятся коктейли на компанию и пунши? Тогда попробуйте этот ягодный, тропический и сладкий коктейль на основе вина, алкогольный и слабоалкогольный. ', 2, 1660, 'Литрового кувшина хватит примерно на 4 хайбола/||/Положи в кувшин нарезанные дольками: половинку апельсина, очищенный киви и клубнику 7 ягод/||/Налей сахарный сироп 50 мл, белое вино сухое 400 мл, кокосовый ликер 250 мл и содовую 100 мл/||/Добавь в кувшин немного льда и аккуратно размешай коктейльной ложкой/||/Дай сангрии настояться примерно 15 минут и пей', 'слабоалкогольные, тропические, сладкие, ягодные, на вине', 193, 'sweet', 1, 0, 0, 'Кокосовая сангрия в кувшине.jpg');
