INSERT INTO cocktails (id, name, description, degree, volume, receipt, cocktail_group, basis_id, taste, is_updatable, is_deleted, is_favourite, picture) VALUES (193, 'Кальвадос на сливе', 'Вам нравятся коктейли на компанию? Тогда попробуйте этот ягодный, фруктовый и пряный коктейль на основе кальвадоса, алкогольный и крепкий. ', 0, 903, 'Наполни двухлитровую банку ягодами терна на 3/4, не утрамбовывая/||/Добавь сахарную пудру 200 г, гвоздику 2 шт. и долей кальвадос доверху/||/Закрой банку крышкой и настаивай 3 недели/||/Настойка готова, когда ягоды начали всплывать', 'крепкие, пряные, фруктовые, ягодные, на кальвадосе', 16, 'spicy', 1, 0, 0, 'Кальвадос на сливе.jpg');
