INSERT INTO cocktails (id, name, description, degree, volume, receipt, cocktail_group, basis_id, taste, is_updatable, is_deleted, is_favourite, picture) VALUES (236, 'Ирландский пьяный пунш', 'Вам нравятся физзы и пунши с нестандартной подачей? Тогда попробуйте этот слабоалкогольный цитрусовый, пряный и сладкий коктейль на основе виски.', 0, 2470, 'Пятилитровой чаши для пунша хватит приблизительно на 20–25 порций/||/Нарежь 4 груши на слайсы и брось в чашу/||/Добавь нарезанные на кружки 2 апельсина и 2 лимона, корицу 10 палочек и кардамон 60 зерен/||/Налей лимонный сок 100 мл, апельсиновый сок 150 мл, сироп корицы 200 мл и ирландский виски 1000 мл/||/Добавь апельсиновый биттер 60 дэш/||/Долей лимонад дюшес доверху и аккуратно размешай коктейльной ложкой/||/Разлей по роксам, предварительно наполнив их кубиками льда/||/Укрась слайсом груши и мускатным орехом молотым', 'слабоалкогольные, цитрусовые, сладкие, пряные, на виски', 212, 'citrus', 1, 0, 0, 'Ирландский пьяный пунш.jpg');
INSERT INTO cocktails (id, name, description, degree, volume, receipt, cocktail_group, basis_id, taste, is_updatable, is_deleted, is_favourite, picture) VALUES (237, 'Райский пунш', 'Вам нравятся пунши на компанию? Тогда попробуйте этот ягодный, цитрусовый, тропический и сладкий коктейль на основе ликера, алкогольный и слабоалкогольный. ', 0, 1050, 'Литрового кувшина хватит примерно на 4 хайбола/||/Положи в кувшин малину 10 ягод и подави мадлером/||/Налей лаймовый сок 45 мл, чай эрл грей 200 мл, яблочный сок 100 мл, сахарный сироп 25 мл и кокосовый ликер 150 мл/||/Наполни кувшин кубиками льда и аккуратно размешай коктейльной ложкой/||/Укрась малиной и кружками лайма', 'слабоалкогольные, сладкие, цитрусовые, тропические, ягодные', 672, 'sweet', 1, 0, 0, 'Райский пунш.jpg');
