INSERT INTO cocktails (id, name, description, degree, volume, receipt, cocktail_group, basis_id, taste, is_updatable, is_deleted, is_favourite, picture) VALUES (239, 'Уэкфилдский треугольник', 'Вам нравятся физзы? Тогда попробуйте этот травяной, пряный и сладкий коктейль на основе игристого, алкогольный и слабоалкогольный. ', 2, 580, 'Положи в питчер нарезанный ревень 3 стебля и насыпь сахарный песок 20 к. ложек/||/Помешивая, потоми на огне 10 минут/||/Перелей в блендер и взбей/||/Налей в стакан для смешивания получившееся ревеневое пюре 9 к. ложек, лаймовый сок 10 мл и сахарный сироп 15 мл/||/Добавь стручок ванили/||/Наполни стакан кубиками льда и размешай коктейльной ложкой/||/Перелей через стрейнер в охлажденный коктейльный бокал/||/Долей просекко доверху и аккуратно размешай коктейльной ложкой/||/Укрась половинкой стебля ревеня', 'слабоалкогольные, пряные, травяные, сладкие, на игристом', 187, 'spicy', 1, 0, 0, 'Уэкфилдский треугольник.jpg');
INSERT INTO cocktails (id, name, description, degree, volume, receipt, cocktail_group, basis_id, taste, is_updatable, is_deleted, is_favourite, picture) VALUES (240, 'Топ банана', 'Вам нравятся шоты? Тогда попробуйте этот тропический, шоколадный и сладкий коктейль на основе водки, алкогольный и крепкий. ', 12, 50, 'Налей в стопку банановый сироп желтый 15 мл/||/Используя коктейльную ложку, уложи слоями кофейный ликер 10 мл, какао ликер светлый 10 мл и водку 15 мл/||/Подожги и быстро выпей через трубочку', 'крепкие, тропические, шоколадные, сладкие, на водке', 7, 'sweet', 1, 0, 0, 'Топ банана.jpg');
INSERT INTO cocktails (id, name, description, degree, volume, receipt, cocktail_group, basis_id, taste, is_updatable, is_deleted, is_favourite, picture) VALUES (241, 'Московская лихорадка', 'Вам нравятся горячие коктейли? Тогда попробуйте этот слабоалкогольный ягодный и мятный коктейль на основе джина.', 8, 182, 'Положи в бокал для ирландского кофе облепиху 4 к. ложки и мяту 6 листиков/||/Налей сироп фундук 20 мл и джин 40 мл/||/Долей горячий чай ассам доверху и аккуратно размешай коктейльной ложкой/||/Укрась веточкой мяты', 'слабоалкогольные, ягодные, мятные, на джине, горячие', 8, 'neutral', 1, 0, 0, 'Московская лихорадка.jpg');
