INSERT INTO cocktails (id, name, description, degree, volume, receipt, cocktail_group, basis_id, taste, is_updatable, is_deleted, is_favourite, picture) VALUES (243, 'Чарльз сазерак', 'Вам нравится сазерак? Тогда попробуйте Чарльз сазерак. Это крепкий травяной коктейль на основе коньяка и бурбона.', 32, 123, 'Положи в рокс кубик тростникового сахара, пропитанный ангостурой биттером 1 дэш, и подави мадлером/||/Налей в бокал абсент 5 мл, бурбон 60 мл и выдержанный коньяк 30 мл/||/Добавь пишо биттер 5 дэш/||/Положи в рокс ледяной куб и аккуратно размешай коктейльной ложкой/||/Добавь воду без газа 15 мл/||/Укрась апельсиновой цедрой', 'крепкие, травяные, на бурбоне, на коньяке, твисты', 1, 'herbal', 1, 0, 0, 'Чарльз сазерак.jpg');
INSERT INTO cocktails (id, name, description, degree, volume, receipt, cocktail_group, basis_id, taste, is_updatable, is_deleted, is_favourite, picture) VALUES (244, 'Цветочный магазин Кейти', 'Вам нравятся сауэры? Тогда попробуйте этот кислый и цветочный коктейль на основе текилы, алкогольный и крепкий. ', 5, 305, 'Смажь коктейльный бокал пастисом/||/Налей в шейкер лимонный сок 15 мл, белок перепелиного яйца 15 мл и воду из цветков апельсина 1 дэш/||/Добавь лавандовый биттер 2 дэш, ликер из цветков бузины 20 мл и золотую текилу 45 мл/||/Тщательно взбей безо льда/||/Наполни шейкер кубиками льда и взбей снова/||/Перелей через стрейнер в охлажденный бокал/||/Укрась несколькими каплями ангостуры биттер и цветком орхидеи', 'крепкие, кислые, цветочные, на текиле, сауэры', 13, 'sour', 1, 0, 0, 'Цветочный магазин Кейти.jpg');
INSERT INTO cocktails (id, name, description, degree, volume, receipt, cocktail_group, basis_id, taste, is_updatable, is_deleted, is_favourite, picture) VALUES (245, 'Облако в штанах', 'Вам нравятся лонги и физзы? Тогда попробуйте этот крепкий, кислый и цветочный коктейль на основе джина с добавлением игристого.', 4, 447, 'Налей в стакан для смешивания сахарный сироп 15 мл и лимонный сок 15 мл/||/Добавь лавандовый биттер 2 дэш, домашний джин на клитории 30 мл и просекко 80 мл/||/Наполни стакан кубиками льда и аккуратно размешай коктейльной ложкой/||/Перелей через стрейнер во флюте/||/Укрась бокал пищевым сусальным золотом', 'крепкие, кислые, цветочные, на джине, на игристом', 187, 'sour', 1, 0, 0, 'Облако в штанах.jpg');
INSERT INTO cocktails (id, name, description, degree, volume, receipt, cocktail_group, basis_id, taste, is_updatable, is_deleted, is_favourite, picture) VALUES (246, 'Тигровая лилия', 'Вам нравятся шорты? Тогда попробуйте этот фруктовый, тропический и сладкий коктейль на основе водки, алкогольный и крепкий. ', 4, 462, 'Выжми сок из стручкового перца 100 г/||/Положи в шейкер пюре манго 20 г и пюре маракуйи 20 г/||/Налей лаймовый сок 10 мл и ванильный сироп 15 мл/||/Добавь базиликовую тинктуру 5 дэш и домашнюю водку на сычуаньском перце 45 мл/||/Наполни шейкер кубиками льда и тщательно взбей/||/Перелей через стрейнер в охлажденное шампанское блюдце/||/Долей просекко доверху/||/Укрась листиком зеленого базилика и шафраном', 'крепкие, фруктовые, тропические, сладкие, на водке', 48, 'sweet', 1, 0, 0, 'Тигровая лилия.jpg');
