INSERT INTO cocktails (id, name, description, degree, volume, receipt, cocktail_group, basis_id, taste, is_updatable, is_deleted, is_favourite, picture) VALUES (249, 'Горячий и острый', 'Вам нравятся горячие коктейли? Тогда попробуйте этот пряный, сладкий и пикантный коктейль на основе коньяка, алкогольный и слабоалкогольный. ', 5, 282, 'Положи в питчер мелко нарезанный перчик чили/||/Налей сахарный сироп 10 мл и воду без газа 100 мл/||/Помешивая, доведи до кипения/||/Дай остыть и повтори операцию 2–3 раза/||/Налей в питчер лимонный сок 15 мл, сок корня имбиря 5 мл, медовый сироп 10 мл и коньяк 40 мл/||/Добавь гвоздику 2 шт. и, помешивая, нагрей, не доводя до кипения/||/Перелей через ситечко в бокал для ирландского кофе/||/Укрась кружком имбиря, проткнутым чили перцем', 'слабоалкогольные, пряные, сладкие, пикантные, на коньяке', 10, 'spicy', 1, 0, 0, 'Горячий и острый.jpg');
