INSERT INTO cocktails (id, name, description, degree, volume, receipt, cocktail_group, basis_id, taste, is_updatable, is_deleted, is_favourite, picture) VALUES (257, 'Малиновый пиджак шутер', 'Вам нравятся шоты? Тогда попробуйте этот сладкий коктейль на основе водки, алкогольный и крепкий. ', 24, 83, 'Налей в стопку домашнюю малиновую водку 50 мл/||/Налей в другую стопку черносмородиновый сироп 25 мл и окуни в него курагу на шпажке/||/Выпей шот и заверши его курагой', 'крепкие, сладкие, на водке, шоты, разноцветные', 33, 'sweet', 1, 0, 0, 'Малиновый пиджак шутер.jpg');
