INSERT INTO cocktails (id, name, description, degree, volume, receipt, cocktail_group, basis_id, taste, is_updatable, is_deleted, is_favourite, picture) VALUES (260, 'Виноградный мохито', 'Вам нравятся твисты на классику, лонги и физзы? Тогда попробуйте этот ягодный, цитрусовый и мятный коктейль на основе рома, алкогольный и слабоалкогольный. ', 5, 388, 'Положи в слинг зеленый виноград 5 ягод, разрезанных пополам, и мяту 10 листьев/||/Добавь сахарный песок 2 к. ложки и подави мадлером/||/Наполни слинг дробленым льдом доверху/||/Налей лаймовый сок 15 мл и белый ром 50 мл/||/Долей содовую доверху и аккуратно размешай коктейльной ложкой/||/Досыпь немного дробленого льда/||/Укрась ягодами зеленого винограда и веточкой мяты', 'слабоалкогольные, мятные, ягодные, цитрусовые, на роме', 9, 'citrus', 1, 0, 0, 'Виноградный мохито.jpg');
