INSERT INTO cocktails (id, name, description, degree, volume, receipt, cocktail_group, basis_id, taste, is_updatable, is_deleted, is_favourite, picture) VALUES (296, 'Бурбон на черносливе', 'Вам нравятся шоты на компанию? Тогда попробуйте этот фруктовый и сладкий коктейль на основе бурбона, алкогольный и крепкий. ', 0, 901, 'Наполни двухлитровую банку черносливом, не утрамбовывая/||/Добавь сахарный песок 200 г и долей бурбон доверху/||/Закрой банку крышкой и настаивай 4 дня в темном и теплом месте', 'крепкие, фруктовые, сладкие, на бурбоне, на компанию', 1, 'sweet', 1, 0, 0, 'Бурбон на черносливе.jpg');
