INSERT INTO cocktails (id, name, description, degree, volume, receipt, cocktail_group, basis_id, taste, is_updatable, is_deleted, is_favourite, picture) VALUES (298, 'Монпансье', 'Вам нравятся лонги? Тогда попробуйте этот цитрусовый, пряный и сладкий коктейль на основе водки, алкогольный и крепкий. ', 3, 520, 'Положи в шейкер нарезанные половинки лайма и апельсина и подави мадлером/||/Налей сироп корицы 20 мл и грейпфрутовую водку 40 мл/||/Наполни шейкер кубиками льда и взбей/||/Перелей вместе со льдом в хайбол/||/Досыпь сверху немного дробленого льда/||/Укрась леденцами', 'крепкие, сладкие, пряные, цитрусовые, на водке', 21, 'sweet', 1, 0, 0, 'Монпансье.jpg');
INSERT INTO cocktails (id, name, description, degree, volume, receipt, cocktail_group, basis_id, taste, is_updatable, is_deleted, is_favourite, picture) VALUES (299, 'Роял лимонад', 'Вам нравятся физзы? Тогда попробуйте этот ягодный, цитрусовый, пряный и сладкий коктейль на нескольких видах алкоголя, алкогольный и крепкий. ', 5, 330, 'Положи в бокал для вина малину 4 ягодки и кружок лайма/||/Наполни бокал кубиками льда доверху/||/Налей малиновый сироп 10 мл, ликер трипл сек 10 мл, драмбуи 10 мл, серебряную текилу 10 мл, белый ром 10 мл, джин 10 мл и водку 10 мл/||/Долей спрайт доверху и аккуратно размешай коктейльной ложкой/||/Укрась кружками лайма', 'крепкие, цитрусовые, ягодные, сладкие, пряные', 3, 'citrus', 1, 0, 0, 'Роял лимонад.jpg');
INSERT INTO cocktails (id, name, description, degree, volume, receipt, cocktail_group, basis_id, taste, is_updatable, is_deleted, is_favourite, picture) VALUES (300, 'БричМула', 'Вам нравится мул? Тогда попробуйте БричМула. Это пряный, травяной и сладкий коктейль на основе водки, алкогольный и слабоалкогольный. ', 3, 435, 'Положи в хайбол большую дольку корня имбиря и подави мадлером/||/Наполни бокал кубиками льда доверху/||/Налей лаймовый сок 10 мл, медовый сироп 10 мл, сироп лимонника 30 мл и водку 40 мл/||/Добавь ангостуру биттер 2 дэш/||/Долей имбирное пиво доверху и аккуратно размешай коктейльной ложкой/||/Укрась ананасовым листом и малиной, перетертой с сахаром, в кожуре половинки лайма', 'слабоалкогольные, пряные, травяные, сладкие, на водке', 7, 'spicy', 1, 0, 0, 'БричМула.jpg');
INSERT INTO cocktails (id, name, description, degree, volume, receipt, cocktail_group, basis_id, taste, is_updatable, is_deleted, is_favourite, picture) VALUES (301, 'Собака.ру', 'Вам нравятся шоты? Тогда попробуйте этот кислый и пикантный коктейль на основе водки, алкогольный и крепкий. ', 20, 40, 'Налей в стопку лимонный сок 15 мл/||/Используя коктейльную ложку, уложи слой водки 20 мл/||/Добавь 5–6 капель табаско соуса красного', 'крепкие, пикантные, кислые, на водке, шоты', 7, 'sour', 1, 0, 0, 'Собака.ру.jpg');
INSERT INTO cocktails (id, name, description, degree, volume, receipt, cocktail_group, basis_id, taste, is_updatable, is_deleted, is_favourite, picture) VALUES (302, '48 капель', 'Вам нравятся шорт дринки? Тогда попробуйте этот цитрусовый и сладкий коктейль на основе водки, алкогольный и крепкий. ', 8, 291, 'Налей в шейкер лимончелло 30 мл, херес фино 30 мл и водку 30 мл/||/Наполни шейкер кубиками льда и взбей/||/Перелей через стрейнер в охлажденный коктейльный бокал/||/Укрась апельсиновой цедрой', 'крепкие, цитрусовые, сладкие, на водке, шорт дринки', 7, 'citrus', 1, 0, 0, '48 капель.jpg');
INSERT INTO cocktails (id, name, description, degree, volume, receipt, cocktail_group, basis_id, taste, is_updatable, is_deleted, is_favourite, picture) VALUES (303, 'С Фиделем', 'Вам нравятся шоты? Тогда попробуйте этот крепкий пряный и сладкий коктейль на основе рома.', 14, 80, 'Налей в стопку кофейный ликер 10 мл/||/Используя коктейльную ложку, уложи слоями белый ром 20 мл, южный комфорт 10 мл и ликер трипл сек 10 мл/||/Укрась четвертинкой киви/||/Выпей шот и заверши его киви', 'крепкие, сладкие, пряные, на роме, шоты', 9, 'sweet', 1, 0, 0, 'С Фиделем.jpg');
INSERT INTO cocktails (id, name, description, degree, volume, receipt, cocktail_group, basis_id, taste, is_updatable, is_deleted, is_favourite, picture) VALUES (304, 'Пьяное золото', 'Вам нравятся десертные шоты? Тогда попробуйте этот пряный и сладкий коктейль на основе ликера, алкогольный и крепкий. ', 10, 50, 'Налей в стопку медовый сироп 10 мл и яблочный сок 20 мл/||/Используя коктейльную ложку, уложи слоями ликер корицы 10 мл и южный комфорт 10 мл', 'крепкие, пряные, сладкие, на ликере, десерты', 156, 'spicy', 1, 0, 0, 'Пьяное золото.jpg');
INSERT INTO cocktails (id, name, description, degree, volume, receipt, cocktail_group, basis_id, taste, is_updatable, is_deleted, is_favourite, picture) VALUES (305, 'Короткий медовый месяц', 'Вам нравятся шоты? Тогда попробуйте этот кофейный и сладкий коктейль на основе коньяка, алкогольный и крепкий. ', 10, 55, 'Налей в стопку медовый сироп 10 мл/||/Используя коктейльную ложку, уложи слоями кофейный ликер 15 мл, лимонный сок 15 мл и коньяк 15 мл', 'крепкие, кофейные, сладкие, на коньяке, шоты', 10, 'coffee', 1, 0, 0, 'Короткий медовый месяц.jpg');
INSERT INTO cocktails (id, name, description, degree, volume, receipt, cocktail_group, basis_id, taste, is_updatable, is_deleted, is_favourite, picture) VALUES (306, 'Темный ром с колой', 'Вам нравятся твисты на классику и простые миксы? Тогда попробуйте этот сладкий коктейль на основе рома, алкогольный и слабоалкогольный. ', 5, 400, 'Наполни хайбол кубиками льда доверху/||/Налей лаймовый сок 10 мл и темный ром 50 мл/||/Долей колу доверху и аккуратно размешай коктейльной ложкой/||/Укрась 2 кружочками лайма', 'слабоалкогольные, сладкие, на роме, миксы, лонги', 14, 'sweet', 1, 0, 0, 'Темный ром с колой.jpg');
INSERT INTO cocktails (id, name, description, degree, volume, receipt, cocktail_group, basis_id, taste, is_updatable, is_deleted, is_favourite, picture) VALUES (307, 'Капитано', 'Вам нравятся шорты? Тогда попробуйте этот пряный коктейль на основе писко, алкогольный и крепкий. ', 5, 394, 'Положи ледяной куб в рокс/||/Налей в стакан для смешивания домашний красный вермут с пеканом 35 мл и мескаль 10 мл/||/Добавь ангостуру биттер 2 дэш, ванильный сироп 5 мл и писко 35 мл/||/Наполни стакан кубиками льда и аккуратно размешай коктейльной ложкой/||/Перелей через стрейнер в рокс/||/Укрась апельсиновой цедрой и пеканом', 'крепкие, пряные, на писко, шорт дринки, коричневые', 26, 'spicy', 1, 0, 0, 'Капитано.jpg');
INSERT INTO cocktails (id, name, description, degree, volume, receipt, cocktail_group, basis_id, taste, is_updatable, is_deleted, is_favourite, picture) VALUES (308, 'Сауэр афериста', 'Вам нравятся твисты на классику и сауэры? Тогда попробуйте этот крепкий кислый коктейль на основе соджу.', 4, 316, 'Налей в шейкер белок перепелиного яйца 30 мл, лаймовый сок 20 мл, сахарный сироп 15 мл и ликер юдзу 3 дэш/||/Добавь воду из цветков апельсина 1 дэш, соляную тинктуру 1 дэш, соджу 20 мл и граппу 20 мл/||/Тщательно взбей безо льда/||/Наполни шейкер кубиками льда и взбей еще раз/||/Перелей через стрейнер в охлажденный коктейльный бокал/||/Укрась цветком хризантемы и веточкой тимьяна', 'крепкие, кислые, на соджу, твисты, сауэры', 34, 'sour', 1, 0, 0, 'Сауэр афериста.jpg');
INSERT INTO cocktails (id, name, description, degree, volume, receipt, cocktail_group, basis_id, taste, is_updatable, is_deleted, is_favourite, picture) VALUES (309, 'Олд фешен', 'Хотите попробовать "Олд фешен"? Это крепкий цитрусовый коктейль на основе бурбона с добавлением ангостуры. Один из самых старых напитков, известных барменам. Вкус "Олд фешена" очень мягкий, с небольшой кислинкой за счет добавления апельсина.', 9, 221, 'Положи в рокс дольку апельсина и коктейльную вишню/||/Добавь кубик тростникового сахара, пропитанный ангостурой биттером 1 дэш, и подави мадлером/||/Наполни рокс кубиками льда/||/Найлей бурбон 50 мл и аккуратно размешай коктейльной ложкой', 'крепкие, цитрусовые, на бурбоне, классика, олд фешены', 1, 'citrus', 1, 0, 0, 'Олд фешен.jpg');
INSERT INTO cocktails (id, name, description, degree, volume, receipt, cocktail_group, basis_id, taste, is_updatable, is_deleted, is_favourite, picture) VALUES (310, 'Пача Монако', 'Вам нравятся лонги? Тогда попробуйте этот крепкий цитрусовый, соленый и сладкий коктейль на основе джина. ', 6, 457, 'Положи в харрикейн нарезанные большими кусками дольки грейпфрута, лимона и лайма/||/Добавь щепотку соли и подави мадлером/||/Наполни харрикейн дробленым льдом доверху/||/Налей гренадин 25 мл и кальвадос 25 мл/||/Добавь джин 50 мл и аккуратно размешай коктейльной ложкой/||/Укрась долькой карамболы, кружком киви и двумя вишенками', 'крепкие, сладкие, цитрусовые, соленые, на джине', 8, 'sweet', 1, 0, 0, 'Пача Монако.jpg');
INSERT INTO cocktails (id, name, description, degree, volume, receipt, cocktail_group, basis_id, taste, is_updatable, is_deleted, is_favourite, picture) VALUES (311, 'Цитрус хайбол', 'Вам нравятся простые лонги и физзы? Тогда попробуйте этот сладкий коктейль на основе американского виски, алкогольный и слабоалкогольный. ', 4, 410, 'Наполни хайбол кубиками льда доверху/||/Налей американский виски 50 мл и долей лимонад дюшес доверху/||/Аккуратно размешай коктейльной ложкой и укрась кружками лимона', 'слабоалкогольные, сладкие, на виски, миксы, лонги', 2, 'sweet', 1, 0, 0, 'Цитрус хайбол.jpg');
INSERT INTO cocktails (id, name, description, degree, volume, receipt, cocktail_group, basis_id, taste, is_updatable, is_deleted, is_favourite, picture) VALUES (312, 'Колос', 'Вам нравятся десертные коктейли и физзы? Тогда попробуйте этот сливочный, кофейный и сладкий коктейль на основе джина, алкогольный и слабоалкогольный. ', 3, 557, 'Наполни рокс кубиками льда доверху/||/Налей в шейкер лаймовый сок 15 мл/||/Добавь лакричный биттер 1 дэш и лондонский сухой джин 50 мл/||/Наполни шейкер кубиками льда и тщательно взбей/||/Перелей через стрейнер в рокс/||/Налей в бокал охлажденный эспрессо 30 мл/||/Положи в рокс шарик ванильного мороженого/||/Долей имбирное пиво доверху/||/Укрась цветком орхидеи', 'слабоалкогольные, сливочные, кофейные, сладкие, на джине', 8, 'creamy', 1, 0, 0, 'Колос.jpg');
INSERT INTO cocktails (id, name, description, degree, volume, receipt, cocktail_group, basis_id, taste, is_updatable, is_deleted, is_favourite, picture) VALUES (313, 'Чер­но­смо­ро­ди­но­вый ли­мо­над в кув­ши­не', 'Вам нравятся физзы на компанию? Тогда попробуйте этот ягодный и сладкий коктейль на основе содовой, безалкогольный. ', 0, 1176, 'Литрового кувшина хватит примерно на 4 хайбола/||/Наполни кувшин кубиками льда на 2/3/||/Положи в шейкер черносмородиновый джем 15 к. ложек/||/Налей сироп макадамия 40 мл и лимонный сок 45 мл/||/Наполни шейкер кубиками льда и взбей/||/Перелей через стрейнер в кувшин/||/Долей содовую доверху и аккуратно размешай коктейльной ложкой/||/Укрась ягодами черной смородины и цветком орхидеи', 'безалкогольные, сладкие, ягодные, на содовой, физзы', 672, 'sweet', 1, 0, 0, 'Чер­но­смо­ро­ди­но­вый ли­мо­над в кув­ши­не.jpg');
INSERT INTO cocktails (id, name, description, degree, volume, receipt, cocktail_group, basis_id, taste, is_updatable, is_deleted, is_favourite, picture) VALUES (314, 'Жареный негрони', 'Вам нравится негрони? Тогда попробуйте Жареный негрони. Это горький и сухой коктейль на основе джина, алкогольный и крепкий. ', 23, 952, 'Литровой бутыли хватит примерно на 5 коктейльных бокалов/||/Налей в бутыль красный вермут 300 мл, красный биттер 300 мл и лондонский сухой джин 300 мл/||/Добавь миндальную пасту 50 г и встряхни/||/Положи бутыль в морозилку на 5 часов/||/Отфильтруй через марлю в другую бутыль и поставь в холодильник/||/Подавай в коктейльном бокале/||/Укрась карамельным попкорном на шпажке', 'крепкие, горькие, сухие, на джине, твисты', 8, 'bitter', 1, 0, 0, 'Жареный негрони.jpg');
INSERT INTO cocktails (id, name, description, degree, volume, receipt, cocktail_group, basis_id, taste, is_updatable, is_deleted, is_favourite, picture) VALUES (315, 'Оранжевый бунт', 'Вам нравятся лонги? Тогда попробуйте этот слабоалкогольный фруктовый, цитрусовый, кофейный, тропический и сладкий коктейль на основе текилы.', 3, 555, 'Наполни кубок кубиками льда доверху/||/Положи в шейкер пюре маракуйи 25 г/||/Налей лаймовый сок 10 мл и домашний сироп эспрессо 20 мл/||/Добавь серебряную текилу 45 мл/||/Наполни шейкер кубиками льда и тщательно взбей/||/Перелей через стрейнер в кубок/||/Укрась подсушенным кружком апельсина, кофейными зернами и цветками розмарина', 'слабоалкогольные, цитрусовые, фруктовые, кофейные, тропические', 3, 'citrus', 1, 0, 0, 'Оранжевый бунт.jpg');
INSERT INTO cocktails (id, name, description, degree, volume, receipt, cocktail_group, basis_id, taste, is_updatable, is_deleted, is_favourite, picture) VALUES (316, 'Экзотический грог', 'Вам нравится грог? Тогда попробуйте Экзотический грог. Это фруктовый, цитрусовый, пряный, тропический и сладкий коктейль на основе рома, алкогольный и крепкий. ', 5, 284, 'Положи в бокал для ирландского кофе нарезанные мелкими кубиками дольки апельсина, лимона и зеленого яблока/||/Добавь гвоздику 2 шт., по щепотке молотой корицы и черного перца молотого/||/Налей сироп маракуйи 15 мл, кокосовый сироп 15 мл и белый ром 40 мл/||/Долей горячий черный чай доверху и аккуратно размешай коктейльной ложкой/||/Укрась кружком киви', 'крепкие, пряные, фруктовые, цитрусовые, тропические', 9, 'spicy', 1, 0, 0, 'Экзотический грог.jpg');
