INSERT INTO cocktails (id, name, description, degree, volume, receipt, cocktail_group, basis_id, taste, is_updatable, is_deleted, is_favourite, picture) VALUES (386, 'Шампань фреш', 'Вам нравятся лонги и физзы? Тогда попробуйте этот овощной, пряный и тропический коктейль на основе игристого, алкогольный и слабоалкогольный. ', 2, 720, 'Наполни коллинз дробленым льдом доверху/||/Положи в шейкер нарезанную половину огурца, дольку корня имбиря и подави мадлером/||/Добавь лимонный сок 15 мл и сироп маракуйи 15 мл/||/Наполни шейкер кубиками льда и взбей/||/Перелей через стрейнер в бокал/||/Добавь мякоть четвертинки маракуйи/||/Налей просекко доверху и аккуратно размешай коктейльной ложкой/||/Укрась тонкой полоской огурца на коктейльной шпажке и четвертинкой маракуйи', 'слабоалкогольные, пряные, тропические, овощные, на игристом', 187, 'spicy', 1, 0, 0, 'Шампань фреш.jpg');
