INSERT INTO cocktails (id, name, description, degree, volume, receipt, cocktail_group, basis_id, taste, is_updatable, is_deleted, is_favourite, picture) VALUES (405, 'Кин-дза-дза', 'Вам нравятся коктейли с нестандартной подачей? Тогда попробуйте этот травяной, овощной и сладкий коктейль на основе водки, алкогольный и крепкий. ', 2, 658, 'Срежь верхушку красного сладкого перца и удали из него семечки/||/Наполни перец дробленым льдом доверху/||/Положи в шейкер нарезанную половинку другого красного перца сладкого, нарезанные листья кориандра 10 шт и подави мадлером/||/Налей лимонный сок 15 мл, сахарный сироп 20 мл и водку 40 мл/||/Наполни шейкер кубиками льда и взбей/||/Перелей через стрейнер и ситечко в перец/||/Досыпь немного дробленого льда/||/Укрась веточкой кориандра', 'крепкие, сладкие, овощные, травяные, на водке', 7, 'sweet', 1, 0, 0, 'Кин-дза-дза.jpg');
