INSERT INTO cocktails (id, name, description, degree, volume, receipt, cocktail_group, basis_id, taste, is_updatable, is_deleted, is_favourite, picture) VALUES (424, 'Горячий ром с маслом', 'Вам нравится грог? Тогда попробуйте Горячий ром с маслом. Это крепкий сливочный, пряный, соленый и сладкий коктейль на основе рома.', 7, 211, 'Положи в питчер мед 10 мл, сливочное масло соленое 2 к. ложки и щепотку молотой корицы/||/Налей лимонный сок 15 мл, яблочный сок 60 мл, сахарный сироп 10 мл и темный ром 40 мл/||/Помешивая, нагрей, не доводя до кипения/||/Перелей через ситечко в бокал для ирландского кофе/||/Укрась палочкой корицы и подай с печеньем в лайме', 'крепкие, сливочные, пряные, соленые, сладкие', 14, 'creamy', 1, 0, 0, 'Горячий ром с маслом.jpg');
INSERT INTO cocktails (id, name, description, degree, volume, receipt, cocktail_group, basis_id, taste, is_updatable, is_deleted, is_favourite, picture) VALUES (425, 'Бурбон на малине', 'Вам нравятся шоты на компанию? Тогда попробуйте этот ягодный и сладкий коктейль на основе бурбона, алкогольный и крепкий. ', 0, 901, 'Наполни двухлитровую банку ягодами малины, не утрамбовывая/||/Добавь сахарный песок 200 г и долей бурбон доверху/||/Закрой банку крышкой и настаивай 4 дня в темном и теплом месте', 'крепкие, ягодные, сладкие, на бурбоне, шоты', 1, 'sweet', 1, 0, 0, 'Бурбон на малине.jpg');
INSERT INTO cocktails (id, name, description, degree, volume, receipt, cocktail_group, basis_id, taste, is_updatable, is_deleted, is_favourite, picture) VALUES (426, 'Глинтвейн кардинал', 'Вам нравится глинтвейн? Тогда попробуйте Глинтвейн кардинал. Это крепкий ягодный, пряный и фруктовый коктейль на основе вина.', 9, 339, 'Положи в питчер нарезанную дольку яблока и черносмородиновый джем 8 к. ложек/||/Добавь пару полосок апельсиновой цедры и по штучке бадьяна, корицы в палочках и гвоздики/||/Налей яблочный сок 50 мл, красное вино сухое 200 мл и коньяк 20 мл/||/Помешивая, нагрей, не доводя до кипения/||/Перелей в чайник/||/Подавай на подставке со свечкой, чтобы коктейль не остывал', 'крепкие, ягодные, пряные, фруктовые, на вине', 189, 'spicy', 1, 0, 0, 'Глинтвейн кардинал.jpg');
INSERT INTO cocktails (id, name, description, degree, volume, receipt, cocktail_group, basis_id, taste, is_updatable, is_deleted, is_favourite, picture) VALUES (427, 'Наполеон Динамит', 'Вам нравятся тини-коктейли? Тогда попробуйте этот кислый, фруктовый, цветочный, тропический и сладкий коктейль на основе грушевого бренди, алкогольный и крепкий. ', 4, 276, 'Налей в шейкер ананасовый сок 30 мл и ликер из цветков бузины 10 мл/||/Добавь белый уксус винный 5 мл и грушевый бренди 30 мл/||/Наполни шейкер кубиками льда и тщательно взбей/||/Перелей через стрейнер в охлажденный коктейльный бокал/||/Укрась небольшим количеством укропа', 'крепкие, кислые, цветочные, фруктовые, тропические', 43, 'sour', 1, 0, 0, 'Наполеон Динамит.jpg');
INSERT INTO cocktails (id, name, description, degree, volume, receipt, cocktail_group, basis_id, taste, is_updatable, is_deleted, is_favourite, picture) VALUES (428, 'Умфази', 'Вам нравятся коктейли с нестандартной подачей? Тогда попробуйте этот цитрусовый коктейль на основе джина, алкогольный и крепкий. ', 4, 393, 'Налей в стакан для смешивания домашний лимонный шербет 20 мл и домашний вермут с крыжовником 40 мл/||/Добавь грейпфрутовый биттер 2 дэш, золотой ром 10 мл и лондонский сухой джин 20 мл/||/Наполни стакан для смешивания кубиками льда и аккуратно размешай коктейльной ложкой/||/Перелей коктейль в бутыль через стрейнер/||/Положи в окуриватель лаймовую цедру/||/Помести бутыль и коктейльный бокал под колпак и ароматизируй дымом в течение нескольких минут/||/Укрась пипеткой с домашним виноградным маслом с укропом', 'крепкие, цитрусовые, на джине, театр, желтые', 96, 'citrus', 1, 0, 0, 'Умфази.jpg');
INSERT INTO cocktails (id, name, description, degree, volume, receipt, cocktail_group, basis_id, taste, is_updatable, is_deleted, is_favourite, picture) VALUES (429, 'Диксиленд смэш', 'Вам нравятся лонги? Тогда попробуйте этот кислый, цитрусовый и мятный коктейль на основе водки, алкогольный и крепкий. ', 3, 577, 'Наполни кубок дробленым льдом доверху/||/Положи в шейкер мяту 6 листьев/||/Налей лимонный сок 15 мл, грейпфрутовый сок 30 мл, гренадин 5 мл, медовый сироп 15 мл и водку 50 мл/||/Наполни шейкер кубиками льда и взбей/||/Перелей через стрейнер и ситечко в кубок/||/Досыпь немного дробленого льда/||/Укрась долькой грейпфрута и веточкой мяты', 'крепкие, цитрусовые, кислые, мятные, на водке', 7, 'citrus', 1, 0, 0, 'Диксиленд смэш.jpg');
INSERT INTO cocktails (id, name, description, degree, volume, receipt, cocktail_group, basis_id, taste, is_updatable, is_deleted, is_favourite, picture) VALUES (430, 'Педро пунш', 'Вам нравятся пунши? Тогда попробуйте этот кислый, цитрусовый и пикантный коктейль на основе текилы, алкогольный и крепкий. ', 4, 455, 'Наполни рокс кубиками льда доверху/||/Положи в шейкер колечко перца чили и подави мадлером/||/Налей лимонный сок 15 мл, грейпфрутовый сок 20 мл, сахарный сироп 20 мл и серебряную текилу 50 мл/||/Наполни шейкер кубиками льда и взбей/||/Перелей через стрейнер в рокс/||/Укрась стручком перца чили', 'крепкие, пикантные, кислые, цитрусовые, на текиле', 3, 'sour', 1, 0, 0, 'Педро пунш.jpg');
INSERT INTO cocktails (id, name, description, degree, volume, receipt, cocktail_group, basis_id, taste, is_updatable, is_deleted, is_favourite, picture) VALUES (431, 'Цитрусовая маргарита', 'Вам нравится маргарита? Тогда попробуйте Цитрусовую маргариту. Это кислый и цитрусовый коктейль на основе текилы, алкогольный и крепкий. ', 3, 505, 'Наполни кубок кубиками льда доверху/||/Налей в шейкер лимонный сок 15 мл, лаймовый сок 10 мл, сироп агавы 10 мл и серебряную текилу 50 мл/||/Наполни шейкер кубиками льда и взбей/||/Перелей через стрейнер в кубок/||/Укрась двумя кружками лайма', 'крепкие, цитрусовые, кислые, на текиле, твисты', 3, 'citrus', 1, 0, 0, 'Цитрусовая маргарита.jpg');
INSERT INTO cocktails (id, name, description, degree, volume, receipt, cocktail_group, basis_id, taste, is_updatable, is_deleted, is_favourite, picture) VALUES (432, 'Роллинг слайс', 'Вам нравятся физзы и коктейли с нестандартной подачей? Тогда попробуйте этот сладкий коктейль на основе водки, алкогольный и крепкий. ', 5, 276, 'Положи в охлажденный коктейльный бокал коктейльную вишню и налей водку 40 мл/||/Срежь с апельсина кружок цедры и уложи его поперек коктейльного бокала, отступив немного от слоя водки/||/Аккуратно положи на цедру несколько кубиков льда/||/Налей клюквенный сок 30 мл и содовую доверху/||/Переверни трубочкой цедру, чтобы коктейль смешался, и пей', 'крепкие, сладкие, на водке, театр, физзы', 7, 'sweet', 1, 0, 0, 'Роллинг слайс.jpg');
INSERT INTO cocktails (id, name, description, degree, volume, receipt, cocktail_group, basis_id, taste, is_updatable, is_deleted, is_favourite, picture) VALUES (433, 'Манго дайкири', 'Вам нравится дайкири? Тогда попробуйте Манго дайкири. Это крепкий тропический и сладкий коктейль на основе рома.', 5, 320, 'Налей в шейкер лимонный сок 15 мл, сахарный сироп 15 мл, ликер манго 20 мл и белый ром 40 мл/||/Наполни шейкер кубиками льда и взбей/||/Перелей через стрейнер в охлажденное шампанское блюдце/||/Укрась долькой манго', 'крепкие, тропические, сладкие, на роме, твисты', 9, 'sweet', 1, 0, 0, 'Манго дайкири.jpg');
INSERT INTO cocktails (id, name, description, degree, volume, receipt, cocktail_group, basis_id, taste, is_updatable, is_deleted, is_favourite, picture) VALUES (434, 'Ак-энд-эль', 'Вам нравятся лонги? Тогда попробуйте этот слабоалкогольный сладкий коктейль на основе виски.', 4, 340, 'Наполни кружку кубиками льда доверху/||/Налей шотландский виски 30 мл/||/Добавь лимонный сок 10 мл и медовый сироп 15 мл/||/Долей эль доверху и аккуратно размешай коктейльной ложкой', 'слабоалкогольные, сладкие, на виски, лонги, желтые', 208, 'sweet', 1, 0, 0, 'Ак-энд-эль.jpg');
INSERT INTO cocktails (id, name, description, degree, volume, receipt, cocktail_group, basis_id, taste, is_updatable, is_deleted, is_favourite, picture) VALUES (435, 'Стратосфера', 'Вам нравятся шоты? Тогда попробуйте этот крепкий пряный, мятный и сладкий коктейль на основе самбуки.', 10, 55, 'Налей в стопку мятный ликер зеленый 20 мл/||/Используя коктейльную ложку, уложи слоями самбуку 15 мл, ликер трипл сек 10 мл/||/Добавь лаймовый сок 10 мл/||/Подожги и быстро выпей через трубочку', 'крепкие, мятные, сладкие, пряные, на самбуке', 17, 'sweet', 1, 0, 0, 'Стратосфера.jpg');
INSERT INTO cocktails (id, name, description, degree, volume, receipt, cocktail_group, basis_id, taste, is_updatable, is_deleted, is_favourite, picture) VALUES (436, 'Имбирный виски', 'Вам нравятся простые физзы и лонги? Тогда попробуйте этот слабоалкогольный пряный коктейль на основе виски.', 5, 381, 'Наполни хайбол кубиками льда доверху/||/Налей шотландский виски 50 мл/||/Долей имбирное пиво доверху и аккуратно размешай коктейльной ложкой/||/Выжми лимонную цедру и укрась ею коктейль', 'слабоалкогольные, пряные, на виски, миксы, лонги', 11, 'spicy', 1, 0, 0, 'Имбирный виски.jpg');
INSERT INTO cocktails (id, name, description, degree, volume, receipt, cocktail_group, basis_id, taste, is_updatable, is_deleted, is_favourite, picture) VALUES (437, 'Огуречно-базиличный лимонад в кувшине', 'Вам нравятся коктейли на компанию? Тогда попробуйте этот травяной, овощной, тропический и сладкий коктейль на основе содовой, безалкогольный. ', 0, 1175, 'Литрового кувшина хватит примерно на 4 хайбола/||/Наполни кувшин кубиками льда на 2/3/||/Положи в блендер очищенный и нарезанный небольшой огурец и зеленый базилик 12 листьев/||/Налей лимонный сок 30 мл и сироп личи 50 мл/||/Взбей в блендере без добавления льда/||/Перелей в кувшин/||/Долей содовую доверху и аккуратно размешай коктейльной ложкой/||/Укрась половинками клубники и веточкой зеленого базилика', 'безалкогольные, травяные, сладкие, тропические, овощные', 672, 'herbal', 1, 0, 0, 'Огуречно-базиличный лимонад в кувшине.jpg');
INSERT INTO cocktails (id, name, description, degree, volume, receipt, cocktail_group, basis_id, taste, is_updatable, is_deleted, is_favourite, picture) VALUES (438, 'Чай доброе утро', 'Вам нравятся лонги и физзы? Тогда попробуйте этот сладкий коктейль на основе водки, алкогольный и слабоалкогольный. ', 5, 400, 'Наполни хайбол кубиками льда доверху/||/Налей холодный зеленый чай 50 мл, яблочный сок 50 мл и водку 50 мл/||/Долней спрайт доверху и аккуратно размешай коктейльной ложкой/||/Укрась маленькими дольками зеленого яблока', 'слабоалкогольные, сладкие, на водке, физзы, лонги', 7, 'sweet', 1, 0, 0, 'Чай доброе утро.jpg');
INSERT INTO cocktails (id, name, description, degree, volume, receipt, cocktail_group, basis_id, taste, is_updatable, is_deleted, is_favourite, picture) VALUES (439, 'Сангрита', 'Хотите попробовать "Сангриту"? Это острый овощной чейзер, которым запивают текилу. В составе томатный, апельсиновый и лаймовый соки, огурец, сельдерей и табаско. Пикантный солёный напиток идеален для того, чтобы сопроводить им стопку текилы.', 0, 1178, 'Литрового кувшина сангриты хватит, чтобы запить 20 стопок текилы/||/Положи в кувшин нарезанные маленькими кусочками небольшой огурец 1 шт и половинку стебля сельдерея/||/Налей лаймовый сок 60 мл, апельсиновый сок 300 мл, томатный сок 600 мл и табаско соус красный 20 мл/||/Добавь соль 1 к. ложку и черный перец молотый 1 к. ложку и аккуратно размешай коктейльной ложкой/||/Укрась стеблем сельдерея/||/Разлей серебряную текилу и сангриту по стопкам/||/Выпей текилу и запей сангритой', 'крепкие, соленые, овощные, цитрусовые, пикантные', 3, 'salty', 1, 0, 0, 'Сангрита.jpg');
INSERT INTO cocktails (id, name, description, degree, volume, receipt, cocktail_group, basis_id, taste, is_updatable, is_deleted, is_favourite, picture) VALUES (440, 'Отведи меня домой', 'Вам нравятся лонги и физзы? Тогда попробуйте этот слабоалкогольный ягодный и сладкий коктейль на основе водки.', 3, 512, 'Наполни стакан кубиками льда доверху/||/Налей лаймовый сок 20 мл и водку 40 мл/||/Добавь малиновое пюре 3 к. ложки и долей имбирное пиво доверху/||/Аккуратно размешай коктейльной ложкой/||/Положи в блендер зеленый базилик 5 листиков, щепотку лецитина и молоко 30 мл/||/Взбей до образования пены/||/Укрась коктейль получившейся пеной и листиками мяты', 'слабоалкогольные, сладкие, на водке, лонги, физзы', 7, 'sweet', 1, 0, 0, 'Отведи меня домой.jpg');
INSERT INTO cocktails (id, name, description, degree, volume, receipt, cocktail_group, basis_id, taste, is_updatable, is_deleted, is_favourite, picture) VALUES (441, 'Кровавый братан', 'Вам нравятся твисты на классику? Тогда попробуйте этот овощной коктейль на основе водки, алкогольный и крепкий. ', 5, 385, 'Сделай на бокале окаемку из красного молотого перца/||/Выжми огуречный сок 10 мл и налей его в шейкер/||/Добавь лимонный сок 10 мл, томатный сок 50 мл, соевый соус 10 мл и кунжутное масло 2 дэш/||/Налей мирто 10 мл, маурин квину 20 мл и домашнюю водку на кацуобуси 40 мл/||/Наполни шейкер кубиками льда и тщательно взбей/||/Перелей через стрейнер в охлажденный бокал/||/Укрась кружком лимона', 'крепкие, овощные, на водке, твисты, красные', 86, 'neutral', 1, 0, 0, 'Кровавый братан.jpg');
INSERT INTO cocktails (id, name, description, degree, volume, receipt, cocktail_group, basis_id, taste, is_updatable, is_deleted, is_favourite, picture) VALUES (442, 'Текила по-восточному', 'Вам нравятся лонги? Тогда попробуйте этот крепкий тропический коктейль на основе текилы.', 3, 656, 'Наполни рокс дробленым льдом доверху/||/Положи в шейкер манго 150 г, лимонный сок 15 мл и сироп личи 30 мл/||/Добавь красный биттер 20 мл и золотую текилу 60 мл/||/Наполни шейкер кубиками льда и тщательно взбей/||/Перелей через стрейнер и ситечко в рокс/||/Укрась подсушенным кружком лайма, лимонной травой, мятой и личи', 'крепкие, тропические, на текиле, лонги, желтые', 13, 'neutral', 1, 0, 0, 'Текила по-восточному.jpg');
INSERT INTO cocktails (id, name, description, degree, volume, receipt, cocktail_group, basis_id, taste, is_updatable, is_deleted, is_favourite, picture) VALUES (443, 'Московский мул', '', 5, 380, 'Наполни медную кружку кубиками льда доверху/||/Налей лаймовый сок 10 мл и водку 50 мл/||/Долей имбирное пиво доверху и аккуратно размешай коктейльной ложкой/||/Укрась долькой лайма', 'крепкие, кислые, на водке, классика, физзы', 7, 'sour', 1, 0, 0, 'Московский мул.jpg');
INSERT INTO cocktails (id, name, description, degree, volume, receipt, cocktail_group, basis_id, taste, is_updatable, is_deleted, is_favourite, picture) VALUES (444, 'Белый глинтвейн', 'Вам нравится глинтвейн? Тогда попробуйте Белый глинтвейн. Это пряный, цитрусовый и сладкий коктейль на основе вина, алкогольный и слабоалкогольный. ', 5, 333, 'Положи в питчер четвертинку грейпфрута/||/Налей мед 20 мл и белое вино сухое 150 мл/||/Добавь палочку корицы, стручок ванили и гвоздику 3 шт./||/Помешивая, нагрей, не доводя до кипения/||/Перелей в бокал для ирландского кофе', 'слабоалкогольные, пряные, цитрусовые, сладкие, на вине', 193, 'spicy', 1, 0, 0, 'Белый глинтвейн.jpg');
