INSERT INTO cocktails (id, name, description, degree, volume, receipt, cocktail_group, basis_id, taste, is_updatable, is_deleted, is_favourite, picture) VALUES (594, 'Домашняя маргарита', 'Вам нравится маргарита? Тогда попробуйте Домашнюю маргариту. Это цитрусовый и сладкий коктейль на основе текилы, алкогольный и крепкий. ', 3, 355, 'Наполни чашку кубиками льда доверху/||/Налей лаймовый сок 15 мл, ликер трипл сек 20 мл и золотую текилу 30 мл/||/Долей спрайт доверху и аккуратно размешай коктейльной ложкой/||/Укрась кружком лайма', 'крепкие, цитрусовые, сладкие, на текиле, физзы', 13, 'citrus', 1, 0, 0, 'Домашняя маргарита.jpg');
INSERT INTO cocktails (id, name, description, degree, volume, receipt, cocktail_group, basis_id, taste, is_updatable, is_deleted, is_favourite, picture) VALUES (595, 'Лошадиная шея в сифоне', 'Вам нравятся твисты, физзы и коктейли на компанию? Тогда попробуйте этот пряный коктейль на основе коньяка, алкогольный и слабоалкогольный. ', 4, 1716, 'Одного сифона приблизительно хватит на 4 хайбола/||/Наполни хайбол кубиками льда доверху/||/Налей в сифон лимонный сок 45 мл, сок корня имбиря 25 мл, сахарный сироп 50 мл, имбирное пиво 650 мл и коньяк 200 мл/||/Закрой сифон и взбей 3–4 раза/||/Налей в хайбол коктейль из сифона и укрась лимонной цедрой', 'слабоалкогольные, пряные, на коньяке, твисты, на компанию', 10, 'spicy', 1, 0, 0, 'Лошадиная шея в сифоне.jpg');
