INSERT INTO cocktails (id, name, description, degree, volume, receipt, cocktail_group, basis_id, taste, is_updatable, is_deleted, is_favourite, picture) VALUES (606, 'Мятный глинтвейн', 'Вам нравится глинтвейн? Тогда попробуйте Мятный глинтвейн. Это ягодный, пряный, мятный и сладкий коктейль на основе вина, алкогольный и слабоалкогольный. ', 6, 266, 'Положи в питчер по 2 ягоды клубники, малины и ежевики/||/Добавь мяту 6 листьев, мед 10 мл и подави мадлером/||/Налей красное вино сухое 150 мл и черносмородиновый ликер 10 мл/||/Добавь гвоздику 4 шт. и ангостуру биттер 2 дэш/||/Помешивая, нагрей, не доводя до кипения/||/Перелей через ситечко в бокал для ирландского кофе/||/Воткни в палочку корицы коктейльную шпажку и надень на нее ягоды малины и ежевики/||/Полей ягоды медом и добавь сверху листик мяты', 'слабоалкогольные, мятные, пряные, ягодные, сладкие', 189, 'spicy', 1, 0, 0, 'Мятный глинтвейн.jpg');
