INSERT INTO cocktails (id, name, description, degree, volume, receipt, cocktail_group, basis_id, taste, is_updatable, is_deleted, is_favourite, picture) VALUES (635, 'Кола и грейпфрутовый ликер', 'Вам нравятся простые лонги? Тогда попробуйте этот цитрусовый и сладкий коктейль на основе ликера, алкогольный и слабоалкогольный. ', 0, 410, 'Наполни хайбол кубиками льда доверху/||/Налей грейпфрутовый ликер красный 50 мл/||/Долей колу доверху и аккуратно размешай коктейльной ложкой/||/Укрась маленькой долькой грейпфрута', 'слабоалкогольные, сладкие, цитрусовые, на ликере, миксы', 672, 'sweet', 1, 0, 0, 'Кола и грейпфрутовый ликер.jpg');
INSERT INTO cocktails (id, name, description, degree, volume, receipt, cocktail_group, basis_id, taste, is_updatable, is_deleted, is_favourite, picture) VALUES (636, 'Любовь к Милану', 'Вам нравятся лонги? Тогда попробуйте этот кислый, цитрусовый, травяной и горький коктейль на основе виски, алкогольный и крепкий. ', 6, 290, 'Наполни рокс дробленым льдом доверху/||/Налей в бокал лаймовый сок 15 мл, грейпфрутовый сок 45 мл, сахарный сироп 15 мл, красный биттер 30 мл и односолодовый виски из Хайленда 30 мл/||/Добавь домашний биттер милано 3 дэш/||/Хорошо размешай с помощью свизл стик/||/Досыпь немного дробленого льда/||/Укрась лимонной цедрой и веточкой мяты', 'крепкие, горькие, кислые, травяные, цитрусовые', 46, 'bitter', 1, 0, 0, 'Любовь к Милану.jpg');
