INSERT INTO cocktails (id, name, description, degree, volume, receipt, cocktail_group, basis_id, taste, is_updatable, is_deleted, is_favourite, picture) VALUES (641, 'Грог', 'Хотите попробовать "Грог"? Это согревающий слабоалкогольный коктейль на основе чая и рома с добавлением пряностей и цитрусов. Любимый напиток всех моряков с пряным и сладким вкусом. Может использоваться для профилактики любых болезней - физических и духовных.', 7, 221, 'Положи в бокал для ирландского кофе лимон 2 дольки, гвоздику 3 шт. и кардамон 3 зернышка/||/Добавь лимонный сок 15 мл, сахарный сироп 20 мл и белый ром 40 мл/||/Долей горячий черный чай доверху и аккуратно размешай коктейльной ложкой', 'слабоалкогольные, пряные, сладкие, на роме, горячие', 9, 'spicy', 1, 0, 0, 'Грог.jpg');
