INSERT INTO tools (id, name, description, picture, created_by_user) VALUES (1, 'Рокс', 'Широкий стакан с толстым дном для подачи крепких мужских коктейлей или крепкого алкоголя в чистом виде. Стандартный объем: 200–300 мл.', '', 0);
INSERT INTO tools (id, name, description, picture, created_by_user) VALUES (2, 'Хайбол', 'Высокий бокал для подачи воды, соков или клубных миксов, смешанных методом билд – сразу в стакане. Стандартный объем: 300–400 мл.', '', 0);
INSERT INTO tools (id, name, description, picture, created_by_user) VALUES (3, 'Стопка', 'Небольшой стакан или рюмка, как правило, с прямыми стенками. Предназначен для самых крепких напитков или коктейлей, которые выпиваются залпом. Стандартный объем: 50–100 мл.', '', 0);
INSERT INTO tools (id, name, description, picture, created_by_user) VALUES (4, 'Коктейльный бокал', 'Или мартини, один из первых в мире бокалов для подачи коктейлей безо льда. Перед подачей бокал обязательно охлаждают, а длинная ножка не дает коктейлю согреться от руки. Стандартный объем: 90–160 мл.', '', 0);
INSERT INTO tools (id, name, description, picture, created_by_user) VALUES (5, 'Шампанское блюдце', 'Бокал на высокой тонкой шпильке, по легенде повторяющий силуэт фаворитки французского короля. Служит для подачи шампанского и коктейлей категории тини. Стандартный объем: 200–300 мл.', '', 0);
INSERT INTO tools (id, name, description, picture, created_by_user) VALUES (6, 'Бокал для ирландского кофе', 'Крепкий бокал для подачи горячих грогов, глинтвейнов и коктейлей с кофе, в честь одного из которых он и назван. Стандартный объем: 200–300 мл.', '', 0);
INSERT INTO tools (id, name, description, picture, created_by_user) VALUES (7, 'Слинг', 'Канонический бокал для подачи одноименного коктейля, ставшего визитной карточкой отеля «Раффлс». Стандартный объем: 200–300 мл.', '', 0);
INSERT INTO tools (id, name, description, picture, created_by_user) VALUES (8, 'Бокал для вина', 'Классический немного закругленный бокал традиционно больше, чем бокал для белого вина. Стандартный объем: 200–300 мл.', '', 0);
INSERT INTO tools (id, name, description, picture, created_by_user) VALUES (9, 'Коллинз', 'Высокий бокал для лонг- и микс-дринков. Назван в честь классического коктейля «Том Коллинз». Стандартный объем: 300–400 мл.', '', 0);
INSERT INTO tools (id, name, description, picture, created_by_user) VALUES (10, 'Флюте', 'Светский бокал для коктейлей на основе шампанского и игристых вин. Удлиненная узкая форма, напоминающая тюльпан, сохраняет максимум пузырьков в коктейле, позволяя ему долго оставаться искрящимся. Стандартный объем: 180–250 мл.', '', 0);
INSERT INTO tools (id, name, description, picture, created_by_user) VALUES (11, 'Кувшин', 'Археологический кухонный сосуд для подачи воды, лимонадов и сангрий. Стандартный объем: 500–3000 мл.', '', 0);
INSERT INTO tools (id, name, description, picture, created_by_user) VALUES (12, 'Кубок', 'Королевский бокал с утолщенным стеклом, позволяющийся чокнутся с соседом так, чтобы напитки в бокалах перехлестнулись. Стандартный объем: 200–300 мл.', '', 0);
INSERT INTO tools (id, name, description, picture, created_by_user) VALUES (13, 'Банка с крышкой', 'Большой сосуд с плотно прилегающей крышкой служит для создания и хранения настоек и наливок. Стандартных объемов не существует.', '', 0);
INSERT INTO tools (id, name, description, picture, created_by_user) VALUES (14, 'Харрикейн', 'Антикварная керосиновая лампа-бокал для подачи тропических напитков, названная в честь одноименного классического коктейля от Пэта О’Брайна. Стандартный объем: 300–400 мл.', '', 0);
INSERT INTO tools (id, name, description, picture, created_by_user) VALUES (15, 'Бокал сауэр', 'Маленький бокал для взбитых напитков одноименной группы, созданных на основе цитрусовых, сахара и крепкого алкоголя. Стандартный объем: 90–120 мл.', '', 0);
INSERT INTO tools (id, name, description, picture, created_by_user) VALUES (16, 'Чашка', 'Высокая керамическая чашка для кофе или чая, ставшая в 1930 году символом классического коктейля «Том и Джерри». Стандартный объем: 200–250 мл.', '', 0);
INSERT INTO tools (id, name, description, picture, created_by_user) VALUES (17, 'Медная кружка', 'Фронтовая посуда, ставшая «троянским конем» для водки в Америке и фирменной посудой для подачи классического коктейля «Московский мул». Стандартный объем: 300–400 мл.', '', 0);
INSERT INTO tools (id, name, description, picture, created_by_user) VALUES (18, 'Тики-бокал', 'Керамический истукан, изначально символизирующий полинезийских богов, служит для подачи тики-коктейлей.', '', 0);
INSERT INTO tools (id, name, description, picture, created_by_user) VALUES (19, 'Заварной чайник', 'История античного китайского сосуда началась там же и тогда же, что и история самого чая, – в Китае в XIV веке. Служит для заваривания чая, трав и специй в горячей воде или соке.', '', 0);
INSERT INTO tools (id, name, description, picture, created_by_user) VALUES (20, 'Чаша для пунша', 'Объемный сосуд из стекла, хрусталя или металла для подачи пуншей был привезен из Индии в Англию в начале XVII века. Стандартный объем: 5 л.', '', 0);
INSERT INTO tools (id, name, description, picture, created_by_user) VALUES (21, 'Коньячный бокал', 'Классический бокал для коньяка или бренди. Стандартный объем: 170–400 мл.', '', 0);
INSERT INTO tools (id, name, description, picture, created_by_user) VALUES (22, 'Коктейльная креманка', 'В прошлой жизни десертный бокал для подачи мороженого сегодня выступает в роли фирменного бокала для милкшейкеов, лимонадов и коктейлей. Стандартный объем: 250–350 мл.', '', 0);
INSERT INTO tools (id, name, description, picture, created_by_user) VALUES (23, 'Чайная ложка', 'Ложка для размешивания чая в чашке. В одной ложке помещается в среднем 5 мл жидкости или 5 г сухого ингредиента.', '', 0);
INSERT INTO tools (id, name, description, picture, created_by_user) VALUES (24, 'Поднос', 'Металлический или деревянный лист для подачи еды или напитков.', '', 0);
INSERT INTO tools (id, name, description, picture, created_by_user) VALUES (25, 'Стаканчик для чая', 'Традиционный турецкий стаканчик для чаепития. Стандартный объем: 40–80 мл.', '', 0);
INSERT INTO tools (id, name, description, picture, created_by_user) VALUES (26, 'Блюдце', 'Стеклянный, фарфоровый или металлический предмет посуды, который традиционно подают в паре с чашкой. В современной интерпретации на блюдце подают бокал с коктейлем, благодаря чему подача становится более изысканной и элегантной.', '', 0);
INSERT INTO tools (id, name, description, picture, created_by_user) VALUES (27, 'Бутыль', 'Классический сосуд для хранения алкогольных и безалкогольных напитков.', '', 0);
INSERT INTO tools (id, name, description, picture, created_by_user) VALUES (28, 'Миска', 'Стеклянная или металлическая емкость для взбивания ингредиентов при помощи венчика. Для миксов с добавлением томатного или цитрусового соков используют только стеклянные миски или из нержавеющей стали, не реагирующие на кислоту.', '', 0);
INSERT INTO tools (id, name, description, picture, created_by_user) VALUES (29, 'Бокал маргарита', 'В прошлой жизни соусник для гуакамоле сегодня выступает в роли фирменного бокала для подачи самого популярного в мире коктейля на основе текилы. Стандартный объем: 250–350 мл.', '', 0);
INSERT INTO tools (id, name, description, picture, created_by_user) VALUES (30, 'Френч-пресс', 'Французский аксессуар для заваривания кофе и чая, состоящий из стеклянной колбы и пресса с ситечком.', '', 0);
INSERT INTO tools (id, name, description, picture, created_by_user) VALUES (31, 'Коническая колба', 'Лабораторная колба конической формы с плоским дном и вытянутым горлышком.', '', 0);
INSERT INTO tools (id, name, description, picture, created_by_user) VALUES (32, 'Саке сет', 'Сет для ритуальной подачи японского крепкого алкоголя. Стандартный объем токкури: 250–300 мл, чашечки: 50–60 мл.', '', 0);
INSERT INTO tools (id, name, description, picture, created_by_user) VALUES (33, 'Глиняный горшок', 'Древнейший представитель кухонной утвари. Стандартного объема не существует.', '', 0);
INSERT INTO tools (id, name, description, picture, created_by_user) VALUES (55, 'Питчер', 'Металлический чайничек. Используют для взбивания сливок и приготовления согревающих коктейлей.', '', 0);
INSERT INTO tools (id, name, description, picture, created_by_user) VALUES (34, 'Вилка', 'Столовый прибор, состоящий из рукояти и нескольких узких зубцов (обычно от двух до четырех) на одном конце. Используется для приготовления пищи, а также для удержания или переноски отдельных частей блюда во время еды.', '', 0);
INSERT INTO tools (id, name, description, picture, created_by_user) VALUES (35, 'Бутылка для молока', 'Сосуд для хранения молока, который может использоваться для подачи алкогольных и безалкогольных напитков. Средний объем 500 мл.', '', 0);
INSERT INTO tools (id, name, description, picture, created_by_user) VALUES (36, 'Пробирка', 'Лабораторный сосуд цилиндрической формы с округлым дном. Средний объём 40 мл.', '', 0);
INSERT INTO tools (id, name, description, picture, created_by_user) VALUES (37, 'Нож', 'Незаменимый инструмент в баре и близкий друг любого бар-бека. Используется для нарезки фруктов, а также в качестве украшения.', '', 0);
INSERT INTO tools (id, name, description, picture, created_by_user) VALUES (38, 'Сковорода', 'Древнейшая кухонная утварь, необходимая в баре для фламбирования ягод и фруктов.', '', 0);
INSERT INTO tools (id, name, description, picture, created_by_user) VALUES (39, 'Пластиковый стакан', 'Высокий пластиковый стакан, который используется для употребления напитков «на ходу». Стандартный объем: 350 – 400 мл.', '', 0);
INSERT INTO tools (id, name, description, picture, created_by_user) VALUES (40, 'Пинта', 'Один из старейших бокалов для пива. Также служит единицей измерения, равной 480 мл. У американской версии – ровная конусная форма, английский вариант представляет собой конус с горбинкой, разновидность из Германии сужается кверху. Стандартный объем: 300–600 мл.', '', 0);
INSERT INTO tools (id, name, description, picture, created_by_user) VALUES (41, 'Сет', 'Гастрономический сет для дегустаций. Состоит из стеклянного подноса, наполненного льдом, который сохраняет нужную температуру маленьких пробников.', '', 0);
INSERT INTO tools (id, name, description, picture, created_by_user) VALUES (42, 'Калабас', 'Традиционный сосуд из Латинской Америки для подачи мате. Всегда идет в комплекте с бомбильей. Стандартный объем: 150–200 мл.', '', 0);
INSERT INTO tools (id, name, description, picture, created_by_user) VALUES (43, 'Консервная банка', 'Классический сосуд из тонкого метала для хранения напитков и пищевых продуктов. Стандартного объёма не существует.', '', 0);
INSERT INTO tools (id, name, description, picture, created_by_user) VALUES (44, 'Стеклянный колпак', 'Заимствован бартендерами из арсенала кондитеров и шеф-поваров. Обеспечивает эффектную подачу напитка. Так, под колпак можно запустить клубы ароматного холодного дыма, который дополнит напиток новыми вкусовыми нотами.', '', 0);
INSERT INTO tools (id, name, description, picture, created_by_user) VALUES (45, 'Сливочник', 'Сосуд, который используют преимущественно для подачи сливок. Стандартный объём: 150 – 300 мл.', '', 0);
INSERT INTO tools (id, name, description, picture, created_by_user) VALUES (46, 'Джиггер', 'Гарант мер и идеальных пропорций в коктейле. В арсенале лучших бартендеров всегда представлен в разных версиях: 20–40 мл, 25–50 мл и 30–60 мл.', '', 0);
INSERT INTO tools (id, name, description, picture, created_by_user) VALUES (56, 'Совок для льда', 'Удобный инструмент из поликарбоната или нержавеющей стали, который позволяет набирать определенную порцию льда. Совки различаются размерами и объемами чаши, что позволяет подобрать инструмент под необходимое количество льда.', '', 0);
INSERT INTO tools (id, name, description, picture, created_by_user) VALUES (47, 'Коктейльная ложка', 'Незаменимый инструмент бармена служит для приготовления коктейлей в смесительном стакане и укладки слоев в шотах, используется также как мера измерения, вмещающая 5 мл жидких ингредиентов или 5 г сыпучих.', '', 0);
INSERT INTO tools (id, name, description, picture, created_by_user) VALUES (48, 'Стрейнер', 'Аксессуар, незаменимый в работе с бостонским или французским шейкером. Служит для фильтрации коктейля от косточек цитрусовых, отколовшихся кусочков льда и других незапланированных ингредиентов.', '', 0);
INSERT INTO tools (id, name, description, picture, created_by_user) VALUES (49, 'Пресс для цитрусовых', 'Незаменимый инструмент, помогающий выжимать сок лаймов или лимонов. Несмотря на то что сегодня в большинстве популярных заведений сок цитрусовых выжимают заранее, свежевыжатый сок считается высшим уровнем качества.', '', 0);
INSERT INTO tools (id, name, description, picture, created_by_user) VALUES (50, 'Шейкер', 'Свой нынешний облик получил в конце 1800-х, а особую популярность приобрел в 1920–1930-е годы, примерив десятки стилей и форм. Разделяют коблер со встроенным стрейнером, французский без стрейнера и бостонский, состоящий из двух равных стаканов. Стандартный объем: 500–700 мл.', '', 0);
INSERT INTO tools (id, name, description, picture, created_by_user) VALUES (51, 'Мадлер', 'Незаменимый аксессуар для тропических коктейлей – им давят ягоды и фрукты в шейкере или сразу в бокале.', '', 0);
INSERT INTO tools (id, name, description, picture, created_by_user) VALUES (52, 'Ситечко', 'Используют совместно с шейкером и стрейнером для двойного процеживания коктейля при переливании из шейкера в бокал. Более мелкая, чем у стрейнера, сетка не допускает попадания в напиток даже самых мелких фруктовых косточек и льдинок.', '', 0);
INSERT INTO tools (id, name, description, picture, created_by_user) VALUES (53, 'Нож для цедры', 'Вид оружия бармена из японской культуры карвинга. Служит для срезания тонких полосок цедры, вырезания кружков из яблок или создания широких твистов.', '', 0);
INSERT INTO tools (id, name, description, picture, created_by_user) VALUES (54, 'Стакан для смешивания', 'Классический стакан с барной стойки пятизвездочного отеля. Служит для приготовления крепких мужских напитков без добавления соков, травок и джемов. Лед не деформируется и не успевает растаять, сохраняя крепость напитков. Стандартный объем: 500–900 мл.', '', 0);
INSERT INTO tools (id, name, description, picture, created_by_user) VALUES (57, 'Терка для муската', 'Полезный аксессуар для получения пудры из мускатного ореха или шоколада, реже – мелко натертой цедры цитрусовых.', '', 0);
INSERT INTO tools (id, name, description, picture, created_by_user) VALUES (58, 'Ножницы для перепелиных яиц', 'Специальные ножницы с кольцом для фиксации яйца и лезвием, делающим надрез словно циркулем.', '', 0);
INSERT INTO tools (id, name, description, picture, created_by_user) VALUES (59, 'Спрейер', 'Красивый аксессуар для распыления над коктейлем биттеров и ароматических добавок.', '', 0);
INSERT INTO tools (id, name, description, picture, created_by_user) VALUES (60, 'Свизл стик', 'Небольшая палочка из древесины кварарибии с отростками ветвей, формирующих форму своеобразного миксера. Идеально подходит для смешивания коктейлей, поданных с дробленым льдом прямо в бокале.', '', 0);
INSERT INTO tools (id, name, description, picture, created_by_user) VALUES (61, 'Венчик', 'Незаменим для приготовления пены из сливок для «Ирландского кофе» или взбивания яичного желтка с сахаром для «Том и Джерри». Для смешивания или взбивания ингредиентов в кувшинах используют версию с удлиненной ручкой и маленьким наконечником на конце.', '', 0);
INSERT INTO tools (id, name, description, picture, created_by_user) VALUES (62, 'Слайсер для ананаса', 'Тики-аксессуар позволяет в считанные секунды очистить ананас от кожуры и твердой сердцевины.', '', 0);
INSERT INTO tools (id, name, description, picture, created_by_user) VALUES (63, 'Джулеп стрейнер', 'Незаменимый в работе со смесительным стаканом аксессуар. Служит также бесподобным украшением одноименного классического коктейля. Используют для фильтрации коктейля от отколовшихся кусочков льда и других незапланированных ингредиентов.', '', 0);
INSERT INTO tools (id, name, description, picture, created_by_user) VALUES (64, 'Чайное ситечко', 'Используется для заваривания чая или трав. Реже служит в качестве украшения коктейля.', '', 0);
INSERT INTO tools (id, name, description, picture, created_by_user) VALUES (65, 'Перлини шейкер', 'Шейкер для газации коктейлей. После поступления газа в напиток, ингредиенты встряхивают, как в классическом шейкере, и получают газированную смесь, которую после некоторого ожидания (иначе выплеснется наружу) переливают в бокал.', '', 0);
INSERT INTO tools (id, name, description, picture, created_by_user) VALUES (66, 'Марля', 'Тонкая хлопчатобумажная ткань. Используется для тщательной фильтрации напитков.', '', 0);
INSERT INTO tools (id, name, description, picture, created_by_user) VALUES (67, 'Трубочки', 'В 1888 году владелец фабрики бумажных мундштуков Марвин Стоун придумал лучший аксессуар для распития напитков из бокала. Джозеф Фридман сколотил состояние, доработав их гибким гофрированным участком. Последним этапом стал переход на пластик.', '', 0);
INSERT INTO tools (id, name, description, picture, created_by_user) VALUES (68, 'Коктейльная шпажка', 'Декоративный аксессуар для подачи оливок к мартини или коктейльной вишни к коктейлю «Манхэттен». Появился в барах в конце XIX века благодаря маркетинговому гению Чарльзу Форстеру.', '', 0);
INSERT INTO tools (id, name, description, picture, created_by_user) VALUES (69, 'Прищепки', 'Декоративный аксессуар служит для крепления украшений к бокалу.', '', 0);
INSERT INTO tools (id, name, description, picture, created_by_user) VALUES (70, 'Крафт-бумага', 'Экологически чистый оберточный материал, который может использоваться в качестве декоративного аксессуара.', '', 0);
INSERT INTO tools (id, name, description, picture, created_by_user) VALUES (71, 'Зонтик', 'Яркое бумажное украшение для детского или тропического коктейля.', '', 0);
INSERT INTO tools (id, name, description, picture, created_by_user) VALUES (72, 'Подстаканник', 'Металлическая подставка с ручкой, охватывающая стакан и позволяющая пить горячие напитки, не обжигая рук.', '', 0);
INSERT INTO tools (id, name, description, picture, created_by_user) VALUES (73, 'Ложка для абсента', 'Неотъемлемый атрибут ритуальной подачи французского крепкого напитка. В основную колбу заливают алкоголь с добавлением трав и фруктов. Настоявшись, напиток капля за каплей проходит через сахар на ложке и попадает в бокал.', '', 0);
INSERT INTO tools (id, name, description, picture, created_by_user) VALUES (74, 'Салфетки', 'Хлопковый или бумажный барный платок. Иногда выступает в роли костера для напитков.', '', 0);
INSERT INTO tools (id, name, description, picture, created_by_user) VALUES (75, 'Бомбилья', 'Специальная трубочка для подачи мате. В верхней части оснащена мундштуком, в нижней – ситечком.', '', 0);
INSERT INTO tools (id, name, description, picture, created_by_user) VALUES (76, 'Резинка', 'Изделие из резины или латекса.', '', 0);
INSERT INTO tools (id, name, description, picture, created_by_user) VALUES (77, 'Бубен', 'Цыганско-шаманский ударный музыкальный инструмент, который запросто может быть использован в качестве подноса.', '', 0);
INSERT INTO tools (id, name, description, picture, created_by_user) VALUES (78, 'Фонтан для абсента', 'Неотъемлемый атрибут ритуальной подачи французского крепкого напитка. В основную колбу заливают воду с добавлением трав, фруктов, а иногда и льда. Настоявшись, напиток капля за каплей проходит через сахар на ложке и попадает в бокал с абсентом.', '', 0);
INSERT INTO tools (id, name, description, picture, created_by_user) VALUES (79, 'Шахматная доска', 'Доска с нарисованным игровым полем из черных и белых квадратов. Предназначена для игры в алко-шашки или алко-шахматы. Чаще всего встречаются доски размером 50 x 50 см.', '', 0);
INSERT INTO tools (id, name, description, picture, created_by_user) VALUES (80, 'Фольга', 'Гибкий металлический лист толщиной от 0,001мм до 0,5 мм.', '', 0);
INSERT INTO tools (id, name, description, picture, created_by_user) VALUES (81, 'Шишка', 'Плод хвойных растений с характерным травяным запахом. Используется в качестве украшения.', '', 0);
INSERT INTO tools (id, name, description, picture, created_by_user) VALUES (82, 'Блендер', 'Придуманный в 1922 году Стефеном Поплавским электрический инструмент незаменим для приготовления тики-коктейлей, смузи и замороженных напитков.', '', 0);
INSERT INTO tools (id, name, description, picture, created_by_user) VALUES (83, 'Горелка', 'Аксессуар джентльмена, позволяющий поджечь эфирные масла над знаменитым «Космополитеном», посыпать искрящейся корицей тики-коктейль или просто дать прикурить гостю сигару.', '', 0);
INSERT INTO tools (id, name, description, picture, created_by_user) VALUES (84, 'Кофемашина', 'Автоматическая машина для приготовления кофе разных видов: латте, эспрессо, американо, латте маккиято, капучино и гляссе.', '', 0);
INSERT INTO tools (id, name, description, picture, created_by_user) VALUES (85, 'Соковыжималка', 'Выжимает сок из любых овощей или фруктов. В отличие от ручного пресса, электрический механизм отделяет сок от мякоти с помощью центрифуги.', '', 0);
INSERT INTO tools (id, name, description, picture, created_by_user) VALUES (86, 'Сифон', 'Прибор из толстого стекла или металла для газации напитков углекислым газом. Стандартный объем: 1–2 л.', '', 0);
INSERT INTO tools (id, name, description, picture, created_by_user) VALUES (87, 'Миксер для милкшейков', 'Прибор для взбивания милкшейков. Стакан с необходимыми ингредиентами устанавливают на держатель, двигатель миксера срабатывает автоматически и останавливается, когда стакан убирают.', '', 0);
INSERT INTO tools (id, name, description, picture, created_by_user) VALUES (88, 'Сифон для сливок', 'Прибор из толстого стекла или металла для быстрого взбивания сливок в нежную воздушную пену. Можно также использовать для приготовления разнообразных десертов, сложных соусов, муссов. Стандартный объем: 0,5–1 л.', '', 0);
INSERT INTO tools (id, name, description, picture, created_by_user) VALUES (89, 'Окуриватель', 'Прибор для обогащения крепких напитков дымным ароматом. Коктейль ставят под колпак, под который вставляют шланг от пистолета, наполненного древесными опилками с разными ароматами. Дым от горящего дерева насыщает коктейль новыми красками.', '', 0);
INSERT INTO tools (id, name, description, picture, created_by_user) VALUES (90, 'Бочка', 'Небольшая дубовая бочка для выдерживания напитков в баре.', '', 0);
INSERT INTO tools (id, name, description, picture, created_by_user) VALUES (91, 'Дистиллятор', 'Прибор для испарения жидкости с последующей ее конденсацией путем перегонки из одного сосуда в другой. Используют для очистки алкоголя, изменения концентрации или разделения многокомпонентных смесей.', '', 0);
INSERT INTO tools (id, name, description, picture, created_by_user) VALUES (92, 'Термометр', 'Приспособление для измерения температуры жидкостей, окружающей среды, тела и т.д.', '', 0);
INSERT INTO tools (id, name, description, picture, created_by_user) VALUES (93, 'Сифон для кофе', 'Вакуумное устройство для варки кофе.', '', 0);
INSERT INTO tools (id, name, description, picture, created_by_user) VALUES (94, 'Центрифуга', 'Кулинарную центрифугу используют для обесцвечивания напитков, выделения жиров и насыщения масел вкусом.', '', 0);